import { createSlice } from '@reduxjs/toolkit';

const initialState = "this is the initial notification state"

const notificationSlice = createSlice({
  name: 'notification',
  initialState,
  reducers: {
    displayMessage(state, action) {
      // display a message for five seconds when the user votes for an anecdote
      // first let's just display a message indefinitely
      return action.payload;
    }
  },
});

export const { display } = notificationSlice;
export default notificationSlice.reducer;
