import { connect } from 'react-redux';
import { changeFilter } from '../reducers/filterReducer';

const  Filter = (props) => {
  const dispatch = useDispatch();
  // we'll dispatch an action to the store specifying that the current state of the filtered collection is one that includes anecdotes whose content values contain the current value of the filter

  const handleChange = (event) => {
    dispatch(props.changeFilter(event.target.value));
  };

  const style = {
    marginBottom: 10,
  };

  return (
    <div style={style}>
      filter <input onChange={handleChange} />
    </div>
  );
}

const ConnectedFilter = connect(null, { changeFilter })(Filter);

export default ConnectedFilter;
