import { createSlice } from '@reduxjs/toolkit';

const initialState = ""

const notificationSlice = createSlice({
  name: 'notification',
  initialState,
  reducers: {
    displayNotification(_state, action) {
      return action.payload;
    },
  },
});

export const { displayNotification } = notificationSlice.actions;

export const setNotification = (message, durationInSeconds) => {
  return async dispatch => {
    dispatch(displayNotification(message));
    setTimeout(() => {
      dispatch(displayNotification(''));
    }, (durationInSeconds * 1000));
  }

  // setTimeout returns an integer that can be passed to clearTimeout to cancel the timer
  // the problem is that, when there are multiple changes to the notification state, previous calls to setTimeout will clear subsequent not
}

export default notificationSlice.reducer;
