import { createSlice } from '@reduxjs/toolkit';

const anecdotesAtStart = [
  'If it hurts, do it more often',
  'Adding manpower to a late software project makes it later!',
  'The first 90 percent of the code accounts for the first 90 percent of the development time...The remaining 10 percent of the code accounts for the other 90 percent of the development time.',
  'Any fool can write code that a computer can understand. Good programmers write code that humans can understand.',
  'Premature optimization is the root of all evil.',
  'Debugging is twice as hard as writing the code in the first place. Therefore, if you write the code as cleverly as possible, you are, by definition, not smart enough to debug it.'
]

const getId = () => (100000 * Math.random()).toFixed(0)

const asObject = (anecdote) => {
  return {
    content: anecdote,
    id: getId(),
    votes: 0
  }
}

const orderAnecdotes = anecdotes => (
  anecdotes.sort((anecdoteA, anecdoteB) => (
    anecdoteB.votes - anecdoteA.votes
  ))
);

const initialState = orderAnecdotes(anecdotesAtStart.map(asObject));

const anecdoteSlice = createSlice({
  name: 'anecdotes',
  initialState,
  reducers: {
    newAnecdote(state, action) {
      return orderAnecdotes([...state, action.payload]);
    },
    addVote(state, action) {
      const id = action.payload;

      const anecdoteToChange = state.find(anecdote => anecdote.id === id);

      const changedAnecdote = {
        ...anecdoteToChange,
        votes: anecdoteToChange.votes + 1
      }

      return orderAnecdotes(state.map(anecdote => 
        anecdote.id === id ? changedAnecdote : anecdote
      ));
    }
  },
})

export const { newAnecdote, addVote } = anecdoteSlice.actions;
export default anecdoteSlice.reducer;
