import { createSlice } from '@reduxjs/toolkit';
import anecdoteService from '../services/anecdotes';

  const orderAnecdotes = anecdotes => (
  anecdotes.sort((anecdoteA, anecdoteB) => (
    anecdoteB.votes - anecdoteA.votes
  ))
);

const anecdoteSlice = createSlice({
  name: 'anecdotes',
  initialState: [],
  reducers: {
    async newAnecdote(state, action) {
      const addedAnecdote = anecdoteService.createNew(action.payload);
      return orderAnecdotes([...state, addedAnecdote]);
    },

    addVote(state, action) {
      const id = action.payload;

      const anecdoteToChange = state.find(anecdote => anecdote.id === id);

      const changedAnecdote = {
        ...anecdoteToChange,
        votes: anecdoteToChange.votes + 1
      }

      return orderAnecdotes(state.map(anecdote => 
        anecdote.id === id ? changedAnecdote : anecdote
      ));
    },

    appendAnecdote(state, action) {
      state.push(action.payload);
    },

    setAnecdotes(_state, action) {
      return action.payload;
    },
  },
})

export const { newAnecdote, addVote, appendAnecdote, setAnecdotes } = anecdoteSlice.actions;

export const initializeAnecdotes = () => {
  return async dispatch => {
    const anecdotes = await anecdoteService.getAll();
    dispatch(setAnecdotes(anecdotes));
  }
}

export default anecdoteSlice.reducer;
