import { createSlice } from '@reduxjs/toolkit';

const initialState = "ALL" // should be the same state as the anecdotes
// do we have access to the anecdote state here, or is an action dispatched to the store to filter collection by 'all' when the app starts?

const filterSlice = createSlice({
  name: 'filter',
  initialState,
  reducers: {
    changeFilter(state, action) {
      // we do some standard filtering here
    },
  }
});

export const { filterBy, filterAll } = filterSlice.actions;
export default filterSlice.reducer;
